import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ServiceService } from '../services/service.service';
import { Journey } from '../models/Journey';

@Component({
  selector: 'app-newshore',
  templateUrl: './newshore.component.html',
  styleUrls: ['./newshore.component.scss']
})
export class NewshoreComponent {
  formSearch: FormGroup;
  listJourneys!: Journey[];
  loading: boolean = false;

  constructor(private fb: FormBuilder, private service: ServiceService) {
    this.formSearch = this.fb.group({
      Origin: ['', Validators.required],
      Destination: ['', Validators.required]
    });
  }

  searchJourneys(): void{
    this.loading = true;
    this.service.searchJourneys(this.formSearch.value.Origin, this.formSearch.value.Destination).subscribe((data) => {
      this.loading = false;
      this.listJourneys = data;
      console.log('Llegó la respuesta: ', this.listJourneys);

    })
  }

  changeInput(): void{
    let origin = this.formSearch.value.Origin;
    let destination = this.formSearch.value.Destination;

    if (origin === destination) {
      alert('El Origin y el Destino deben ser diferentes. ' + origin);
      alert('El Origin y el Destino deben ser diferentes. ' + destination);
    }
  }

}
<h1>VUELOS</h1>
<div style="padding: 25px; background-color: beige;" >
  <form [formGroup]="formSearch" style="padding-top: 10px;" (ngSubmit)="searchJourneys()">
    <fieldset>
      <div style="display: inline-block; width: 60%; vertical-align: top;">
        <div class="form-group row">
          <label class="control-label col-md-2">Origen</label>
          <div class="col-md-6">
              <input type="text" class="form-control" formControlName="Origin" [maxlength]="3"
              onkeyup="this.value=this.value.toUpperCase();" onfocus="changeInput()">
          </div>
        </div>
        <div class="form-group row" style="padding-top: 10px;">
          <label class="control-label col-md-2">Destino</label>
          <div class="col-md-6">
              <input type="text" class="form-control" formControlName="Destination" [maxlength]="3"
              onkeyup="this.value=this.value.toUpperCase();">
          </div>
        </div>
      </div>
    </fieldset>
    <div class="form-group" style="padding-top: 10px;">
      <button class="btn btn-success float-right" [disabled]="formSearch.invalid"><i class="fas fa-save"></i>Consultar</button>
    </div>
  </form>
</div>



<div class="row" style="padding-top: 15px;">
  <div class="spinner-border text-primary" role="status" *ngIf="loading">
    <span class="sr-only"></span>
  </div>
</div>
