import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-newshore',
  templateUrl: './newshore.component.html',
  styleUrls: ['./newshore.component.scss']
})
export class NewshoreComponent {
  formSearch: FormGroup;
  isComplete = false;
  loading = false;

  constructor(private fb: FormBuilder) {
    this.formSearch = this.fb.group({
      Origin: ['', Validators.required],
      Destination: ['', Validators.required]
    });
  }

  searchJourneys(): void{
    this.loading = true;
    console.log("INGRESAMOS A LA FUNCIÓN");
    console.log("Formulario: ", this.formSearch);
    console.log(this.formSearch.get('origin'));
  }
}
<h1>VUELOS</h1>

<form [formGroup]="formSearch" style="padding-top: 10px;" (ngSubmit)="searchJourneys()">
  <fieldset>
    <div class="row">
      <div class="col">
        <input type="text" class="form-control" placeholder="Origen" aria-label="Origen" formControlName="origin">
      </div>
      <div class="col">
        <input type="text" class="form-control" placeholder="Destino" aria-label="Destino" formControlName="destination">
      </div>
      <div class="col">
        <button class="btn btn-success float-right"><i class="fas fa-save"></i> Aceptar</button>
      </div>
    </div>
  </fieldset>
</form>
